mapper.delete:
DELETE
FROM faq
WHERE FAQ_NO = #{faqNo}
  and REGR_ID = #{regrId}

mapper.update:
        UPDATE faq
        SET TITLE    = #{title}
          , CONTENT  = #{content}
          , FAQ_TP   = #{faqTp}
          , UPD_DTTM = now()
        WHERE FAQ_NO = #{faqNo}
--           and REGR_ID = #{regrId}

mapper.sql:
AND (TITLE LIKE CONCAT('%',#{keyword},'%' )
OR CONTENT LIKE CONCAT ( '%', #{keyword}, '%' )
)
