mapper.select:
SELECT *
FROM MEMBER
WHERE LGIN_ID = #{lginId}
  AND LGIN_PWD = #{lginPwd};

mapper.insert:
INSERT INTO member ( LGIN_ID
                   , LGIN_PWD
                   , NAME
                   , EMAIL
                   , ROLE)
VALUES ( #{lginId}
       , #{lginPwd}
       , #{name}
       , #{email}
       , #{role})

INSERT INTO mbr_info ( MBR_ID
                     , SHOE_SIZE
                     , MP_NO
                     , BIRTH
                     , SEX
                     , REGR_ID)
VALUES ( #{mbrId}
       , #{shoeSize}
       , #{mpNo}
       , #{birth}
       , #{sex}
       , #{regrId})

mapper.select:
SELECT TOT_PNT
FROM MBR_INFO
WHERE MBR_ID = #{mbrId}

SELECT COUNT(*)
FROM MBR_CPN
WHERE MBR_ID = #{mbrId}
