mapper.insert:
INSERT INTO cart_prod ( CART_ID     # 회원장바구니번호
                      , PROD_ID     # 상품아이디
                      , QTY         # 수량
                      , OPT_COMB_NO # 옵션조합번호
                      , REGR_ID)    # 등록자
SELECT CART_ID
     , #{prodId}
     , #{qty}
     , #{optCombNo}
     , #{regrId}
FROM cart
WHERE MBR_ID = #{mbrId};

mapper.update:
UPDATE CART_PROD
SET QTY = #{qty}
  , UPD_DTTM = now()
  , UPDR_ID = #{updrId}
WHERE CART_PROD_NO = #{cartProdNo}

UPDATE CART_PROD
SET DEL_YN = 'Y'
  , DEL_DTTM = now()
  , DELR_ID = #{delrId}
WHERE CART_PROD_NO IN

    #{cartProdNo}

mapper.delete:
DELETE FROM CART_PROD
WHERE CART_PROD_NO IN

    #{cartProdNo}
