mapper.update:
UPDATE DELIVERY
SET ORD_DTL_NO = #{ordDtlNo}
  , DLV_STUS = #{dlvStus}
  , DLV_TP = #{dlvTp}
  , DISPATCH_DTTM = #{dispatchDttm}
  , PICKUP_DTTM = #{pickupDttm}
  , SHMT_DTTM = #{shmtDttm}
  , CMPL_DTTM = #{cmplDttm}
  , DLV_COM = #{dlvCom}
  , TRCK_NO = #{trckNo}
  , UPD_DTTM = now()
  , UPD_ID = #{updId}
WHERE DLV_NO = #{dlvNo}
