mapper.insert:
INSERT INTO PRODUCT ( CATE_ID
                    , PROD_NM
                    , MODEL_NM
                    , BRND_ID
                    , RLES_DT
                    , DLV_CHG_DTL
                    , PROD_DTL_DESC
                    , MFGD_MATR
                    , MFTCO
                    , MFT_NATN
                    , REP_IMG
                    , STATUS
                    , REGR_ID)
VALUES (#{cateId}
       , #{prodNm}
       , #{modelNm}
       , #{brndId}
       , #{rlesDt}
       , #{dlvChgDtl}
       , #{prodDtlDesc}
       , #{mfgdMatr}
       , #{mftco}
       , #{mftNatn}
       , #{repImg}
       , #{status}
       , #{regrId})

mapper.select:
SELECT P.PROD_ID , P.PROD_NM , P.PROD_DTL_DESC , GET_CD_NM('P0002', P.STATUS) AS STATUS_NM , P.DSPY_YN , (SELECT SUM(INV_QTY) FROM PROD_OPT WHERE PROD_ID = P.PROD_ID) AS TOT_QTY , PRC.BUY_PRC , PRC.SALE_PRC , PRC.DC_PER , GET_DC_PRC(PRC.SALE_PRC, PRC.DC_PER) AS DC_PRC , P.DLV_CHG_DTL , GET_BUY_CNT(P.PROD_ID) AS BUY_CNT , GET_TOT_PNT(P.PROD_ID) AS POINT , GET_TOT_PRC(P.PROD_ID) AS TOT_PRC , CATE.LARGE_NM , CATE.MIDDLE_NM , CATE.SMALL_NM , P.MODEL_NM , BRND.BRND_NM , BRND.BRND_KO_NM , P.MFGD_MATR , P.MFTCO , GET_CD_NM('PR001', P.MFTCO) AS mftcoName , P.MFT_NATN , GET_CD_NM('MC001', P.MFT_NATN) AS mgfNatnName , P.VW_CNT , P.REG_DTTM , P.REGR_ID , P.UPD_DTTM , P.UPDR_ID , P.DEL_DTTM , P.DERT_ID , P.DEL_YN , PRC.SALE_ST_DTTM , PRC.SALE_ED_DTTM , PRC.DC_ST_DTTM , PrC.DC_ED_DTTM FROM PRODUCT P JOIN PRICE PRC on P.PROD_ID = PRC.PROD_ID JOIN CATEGORY CATE ON P.CATE_ID = CATE.CATE_ID JOIN BRAND BRND on BRND.BRND_ID = P.BRND_ID WHERE 1=1
AND STR_TO_DATE(P.${dateType}, '%Y-%m-%d') BETWEEN STR_TO_DATE(#{startDate}, '%Y-%m-%d') AND STR_TO_DATE(#{endDate}, '%Y-%m-%d')
AND P.${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND P.STATUS IN
#{status}
AND CATE.LARGE_NM = #{largeNm}
AND CATE.MIDDLE_NM = #{middleNm}
AND CATE.SMALL_NM = #{smallNM}
ORDER BY P.REG_DTTM DESC LIMIT 100
ORDER BY P.PROD_ID DESC
